import React, { Component } from "react";
import { Card, Tag, Icon } from "antd";
import CommentLesson from "../components/CommentLesson";
import { connect } from "react-redux";
import { getLessonDetail } from "../store/actions/lessons";

const { Meta } = Card;

class Lesson extends Component {
  componentDidMount() {
    this.props.getLessonDetail(this.props.match.params.id);
    console.log(this.props.lesson);
  }
  render() {
    return (
      <div>
        <Card
          style={{ width: "90%" }}
          cover={
            <img
              alt="example"
              style={{ maxHeight: "500px" }}
              src="https://cdn-images-1.medium.com/max/1200/0*ngXgBNNdx6iiWP8q.png"
            />
          }
        >
          <Meta
            title="JavaScript in the world"
            description={
              <div>
                <p>Body lesson</p>
                <div>
                  <span style={{ margin: "4px" }}>
                    34 <Icon type="eye" />
                  </span>

                  <span style={{ margin: "4px" }}>
                    3 <Icon type="wechat" />
                  </span>
                  <span style={{ margin: "4px" }}>4 min ago</span>
                </div>
                <div style={{ margin: "4px" }}>
                  <Tag>JavaScript</Tag>
                  <Tag>Web</Tag>
                </div>
              </div>
            }
          />
        </Card>
        <CommentLesson />
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    lesson: state.lessons.lessonDetail,
    loading: state.lessons.loading
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getLessonDetail: id => dispatch(getLessonDetail(id))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Lesson);
