import React, { Component } from "react";
import Hoc from "../hoc/hoc";
import { List, Card, Row, Col, Icon, Avatar, Tag } from "antd";
import Result from "../components/Result";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { getLessonProfile } from "../store/actions/lessons";

const { Meta } = Card;

export class Profile extends Component {
  render() {
    return (
      <div>
        {console.log(this.props.getLessonProfile)}
        <Hoc>
          <h1>Hi {this.props.username}</h1>
          <List
            size="large"
            dataSource="1"
            renderItem={a => <Result key="1" grade="80" />}
          />
          <hr />
          <h1>Your lessons</h1>
        </Hoc>
        <Row>
          <Col span={10} offset={2}>
            <Card
              style={{ width: 300, marginBottom: "15px" }}
              cover={
                <img
                  alt="example"
                  src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png"
                />
              }
              actions={[
                <span>
                  34 <Icon type="eye" />
                </span>,
                <span>
                  3 <Icon type="wechat" />
                </span>,
                <span>4 min ago</span>,
                <Link to="/lesson/2">More</Link>
              ]}
            >
              <Meta
                avatar={
                  <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                }
                title="Card title"
                description={
                  <div>
                    <p>This is the description</p>
                    <Tag>JavaScript</Tag>
                  </div>
                }
              />
            </Card>
          </Col>
        </Row>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    token: state.auth.token,
    username: state.auth.username,
    lessonProfileList: state.lessons.lessonProfileList
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getLessonProfile: id => dispatch(getLessonProfile(id))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Profile);
