import React, { Component } from "react";
import { Card, Tag, Icon, Spin } from "antd";
import CommentLesson from "../components/CommentLesson";
import { connect } from "react-redux";
import { getLessonDetail } from "../store/actions/lessons";
import TimeAgo from "timeago-react";

const { Meta } = Card;
const antIcon = <Icon type="loading" style={{ fontSize: 24 }} spin />;

class Lesson extends Component {
  componentDidMount() {
    this.props.getLessonDetail(this.props.match.params.id);
  }

  render() {
    const content = this.props.lesson.map(lesson => {
      <div>{lesson.title}</div>;
    });

    return (
      <div>
        {this.props.loading ? (
          <Spin indicator={antIcon} />
        ) : (
          <div>
            {content}
            <Card
              style={{ width: "90%" }}
              cover={
                <img
                  alt="example"
                  style={{ maxHeight: "500px" }}
                  src="https://cdn-images-1.medium.com/max/1200/0*ngXgBNNdx6iiWP8q.png"
                />
              }
            >
              <Meta
                title="JavaScript in the world"
                description={
                  <div>
                    <p>Body lesson</p>
                    <div>
                      <span style={{ margin: "4px" }}>
                        34 <Icon type="eye" />
                      </span>

                      <span style={{ margin: "4px" }}>
                        3 <Icon type="wechat" />
                      </span>
                      <span style={{ margin: "4px" }}>
                        {/* <TimeAgo datetime={} /> */}
                      </span>
                    </div>
                    <div style={{ margin: "4px" }}>
                      <Tag>JavaScript</Tag>
                      <Tag>Web</Tag>
                    </div>
                  </div>
                }
              />
            </Card>
            <CommentLesson />
          </div>
        )}
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    lessonDetail: state.lessons.lessonDetail,
    loading: state.lessons.loading
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getLessonDetail: id => dispatch(getLessonDetail(id))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Lesson);
